.<template>
    <div class="home">
      <el-form-item>
        <el-button type="primary" @click="toLogin">立即登录</el-button>
        <el-button @click="toRegister">没有账号，立即注册</el-button>
        <router-link tag="el-button" to="/register">没有账号，立即注册</router-link>
      </el-form-item>
    </div>
</template>

<script>
export default {
 methods: {
    toLogin() {
      this.$router.push("/login");
    },
    toRegister() {
      this.$router.push("/register");
    },
  },
}
</script>

<style scoped >
.login {
  width: 600px;
  height: 100%;
  margin: auto;
  display: flex;
  align-items: center;
  border: gray solid 3px;
  border-radius: 20px;
  background-image: linear-gradient(to top, #cfd9df 0%, #e2ebf0 100%);
}
</style>
.<template>
  <div class="login">
    <el-form :model="ruleForm" :rules="rules" ref="ruleForm" label-width="100px" class="demo-ruleForm">
      <h1>汽车管理系统 | 登录</h1>
      <el-form-item label="用户名" prop="username">
        <el-input prefix-icon="el-icon-user" v-model="ruleForm.username"></el-input>
      </el-form-item>
      <el-form-item label="密码" prop="password">
        <el-input prefix-icon="el-icon-lock" :show-password="true" v-model="ruleForm.password"></el-input>
      </el-form-item>
      <el-form-item>
        <el-button type="primary" @click="submitForm('ruleForm')">立即登录</el-button>
        <el-button @click="toRegister">没有账号，立即注册</el-button>
        <router-link tag="el-button" to="/register">没有账号，立即注册</router-link>
      </el-form-item>
    </el-form>
  </div>
</template>

<script>
import {login} from "../../api/index.js"
import {setCookie} from "../../utils/cookie.js"
export default {
  data() {
    return {
      ruleForm: {
        username: "",
        password: "",
      },
      rules: {
        username: [
          { required: true, message: "请输入您的用户名", trigger: "blur" },
          // { min: 3, max: 8, message: "长度在 3 到 8 个字符", trigger: "blur" },
        ],
        password: [
          { required: true, message: "请输入密码", trigger: "blur" },
          // { min: 8, max: 16, message: "长度在 8 到 16 个字符", trigger: "blur" },
        ],
      },
    };
  },
  methods: {
    submitForm(formName) {
      this.$refs[formName].validate((valid) => {
        if (valid) {
          login(this.ruleForm).then((res) => {
            console.log(res);
            if (res.success) {
              this.$message({
                message: "恭喜你，登录成功!!",
                type: "success",
                duration: 1000,
              });
              setCookie('token',res.data);
                this.$router.push({
                  path: "/index",
                });
            } else {
              this.$message({
                message: res.data.msg,
                type: "error",
                duration: 1000,
              });
            }
          });
        } else {
          console.log("error submit!!");
          return false;
        }
      });
    },
    toRegister() {
      this.$router.push("/register");
    },
  },
};
</script>

<style scoped >
.login {
  width: 600px;
  height: 100%;
  margin: auto;
  display: flex;
  align-items: center;
  border: gray solid 3px;
  border-radius: 20px;
  background-image: linear-gradient(to top, #cfd9df 0%, #e2ebf0 100%);
}
/* .demo-ruleForm{
    background-color: rgb(54, 54, 54);
} */
h1 {
  margin: 15px 0 15px 50px;
  color: rgb(34, 181, 218);
  text-align: center;
}
</style>
